import { useState } from "react";
import { Wand2, Download, RefreshCw, Sparkles } from "lucide-react";

interface ResultPreviewProps {
  uploadedImage: string | null;
  selectedClothing: number | null;
}

export function ResultPreview({ uploadedImage, selectedClothing }: ResultPreviewProps) {
  const [isGenerating, setIsGenerating] = useState(false);
  const [generatedImage, setGeneratedImage] = useState<string | null>(null);

  const canGenerate = uploadedImage && selectedClothing;

  const handleGenerate = async () => {
    if (!canGenerate) return;

    setIsGenerating(true);
    // Simulate AI generation
    await new Promise(resolve => setTimeout(resolve, 3000));
    // For demo, we'll just use the uploaded image
    setGeneratedImage(uploadedImage);
    setIsGenerating(false);
  };

  return (
    <div className="glass rounded-3xl p-6 md:p-8 sticky top-8">
      <div className="flex items-center gap-3 mb-6">
        <div className="w-10 h-10 rounded-xl bg-gradient-primary flex items-center justify-center">
          <Wand2 className="w-5 h-5 text-primary-foreground" />
        </div>
        <div>
          <h3 className="text-lg font-semibold text-foreground">AI 换装效果</h3>
          <p className="text-sm text-muted-foreground">生成你的专属试穿效果</p>
        </div>
      </div>

      {/* Preview Area */}
      <div className="aspect-[3/4] rounded-2xl overflow-hidden bg-muted/50 mb-6">
        {generatedImage ? (
          <div className="relative w-full h-full">
            <img
              src={generatedImage}
              alt="Generated Result"
              className="w-full h-full object-cover"
            />
            <div className="absolute top-3 left-3 px-3 py-1.5 rounded-lg glass text-xs font-medium text-foreground">
              <Sparkles className="w-3 h-3 inline mr-1.5" />
              AI 生成
            </div>
          </div>
        ) : isGenerating ? (
          <div className="w-full h-full flex flex-col items-center justify-center gap-4">
            <div className="relative">
              <div className="w-20 h-20 rounded-full border-4 border-primary/30 border-t-primary animate-spin" />
              <Sparkles className="absolute inset-0 m-auto w-8 h-8 text-primary animate-pulse" />
            </div>
            <div className="text-center">
              <p className="text-foreground font-medium">AI 正在创作中...</p>
              <p className="text-sm text-muted-foreground">请稍候片刻</p>
            </div>
          </div>
        ) : (
          <div className="w-full h-full flex flex-col items-center justify-center gap-4 p-6 text-center">
            <div className="w-20 h-20 rounded-2xl bg-muted flex items-center justify-center">
              <Wand2 className="w-10 h-10 text-muted-foreground" />
            </div>
            <div>
              <p className="text-foreground font-medium mb-1">等待生成</p>
              <p className="text-sm text-muted-foreground">
                {!uploadedImage && !selectedClothing
                  ? "请先上传照片并选择服装"
                  : !uploadedImage
                    ? "请上传你的照片"
                    : "请选择想要试穿的服装"
                }
              </p>
            </div>
          </div>
        )}
      </div>

      {/* Action Buttons */}
      <div className="space-y-3">
        <button
          onClick={handleGenerate}
          disabled={!canGenerate || isGenerating}
          className={`
            w-full py-4 rounded-xl font-semibold flex items-center justify-center gap-2
            transition-all duration-300
            ${canGenerate && !isGenerating
              ? "bg-gradient-primary text-primary-foreground shadow-glow hover:shadow-glow-lg hover:scale-[1.02]"
              : "bg-muted text-muted-foreground cursor-not-allowed"
            }
          `}
        >
          {isGenerating ? (
            <>
              <RefreshCw className="w-5 h-5 animate-spin" />
              生成中...
            </>
          ) : (
            <>
              <Sparkles className="w-5 h-5" />
              生成换装效果
            </>
          )}
        </button>

        {generatedImage && (
          <button
            className="w-full py-4 rounded-xl font-semibold glass text-foreground flex items-center justify-center gap-2 hover:bg-card/80 transition-all duration-300"
          >
            <Download className="w-5 h-5" />
            下载图片
          </button>
        )}
      </div>
    </div>
  );
}

import { Zap, Shield, Palette, Smartphone } from "lucide-react";

const features = [
  {
    icon: Zap,
    title: "极速生成",
    description: "先进的 AI 模型，秒级生成逼真换装效果",
  },
  {
    icon: Shield,
    title: "隐私保护",
    description: "照片仅用于生成，不会被存储或分享",
  },
  {
    icon: Palette,
    title: "海量服装",
    description: "涵盖各类风格，从正装到休闲应有尽有",
  },
  {
    icon: Smartphone,
    title: "随时随地",
    description: "支持手机和电脑，随时体验虚拟试衣",
  },
];

export function Features() {
  return (
    <section id="features" className="py-20 px-4">
      <div className="container mx-auto max-w-6xl">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-foreground mb-4">
            为什么选择我们
          </h2>
          <p className="text-muted-foreground max-w-2xl mx-auto">
            我们运用最先进的 AI 技术，为你带来前所未有的虚拟试衣体验
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {features.map((feature, index) => (
            <div
              key={index}
              className="group p-6 rounded-2xl glass hover:bg-card/80 transition-all duration-300 hover:scale-[1.02]"
            >
              <div className="w-12 h-12 rounded-xl bg-gradient-primary/20 flex items-center justify-center mb-4 group-hover:bg-gradient-primary group-hover:shadow-glow transition-all duration-300">
                <feature.icon className="w-6 h-6 text-primary group-hover:text-primary-foreground transition-colors" />
              </div>
              <h3 className="text-lg font-semibold text-foreground mb-2">
                {feature.title}
              </h3>
              <p className="text-sm text-muted-foreground">
                {feature.description}
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

